INSERT INTO `node_revisions` VALUES ('4029', '4031', '32', 'Eric van den Broek begonnen aan \"kennismakingsronde\"', '<p><img alt=\"\" src=\"/sites/default/files/file/nieuws/800px-Eric_vd_Broek_0.jpg\" style=\"width: 400px; height: 300px; float: left; margin: 3px;\" />Eric van den Broek, die vanaf&nbsp;1 januari samen met Pieter van Boom de&nbsp;nieuwe directie&nbsp;vormt van LievenseCSO holding, is afgelopen maandag begonnen met zijn kennismakingsronde. Hij trapte af&nbsp;in Breda, waar&nbsp;hij direct de taart aan mocht snijden.</p>\r\n<p>Ook op de vestigingen van Bartels zal Eric de komende&nbsp;periode&nbsp;zijn gezicht laten zien om zich&nbsp;voor te stellen en kennis te maken.&nbsp;</p>\r\n<p>Mocht je vragen aan Eric hebben dan kun je ze dan stellen of hem een mail sturen via <a href=\"mailto:EvdBroek@LievenseCSO.com\">EvdBroek@LievenseCSO.com</a>.&nbsp;</p>\r\n', '<p><img alt=\"\" src=\"/sites/default/files/file/nieuws/800px-Eric_vd_Broek_0.jpg\" style=\"width: 400px; height: 300px; float: left; margin: 3px;\" />Eric van den Broek, die vanaf&nbsp;1 januari samen met Pieter van Boom de&nbsp;nieuwe directie&nbsp;vormt van LievenseCSO holding, is afgelopen maandag begonnen met zijn kennismakingsronde.', '', '1452252108', '2');
