INSERT INTO `node_revisions` VALUES ('3848', '3850', '32', 'Nieuw huisstijl: briefpapier & enveloppen', '<p><img alt=\"\" src=\"/sites/default/files/file/nieuws/IMG_4444_0.jpg\" />In navolging&nbsp;van de nieuwe huisstjil voor de visitekaartjes, gaan we morgen (vrijdag 15 augustus) ook over naar nieuw briefpapier en enveloppen.&nbsp;</p>\r\n<p>De gele balk komt nu overal terug en we nemen &quot;afscheid&quot; van de linkerkantlijn op het&nbsp;papier.&nbsp;</p>\r\n<p>Alle&nbsp;vestigingen&nbsp;zijn inmiddels voorzien van het&nbsp;nieuwe papier en de enveloppen en morgen worden officieel de sjablonen in Lotus Notes veranderd&nbsp;in de nieuwe stijl.</p>\r\n<p>Vanaf morgen zul je dus&nbsp;gebruik moeten maken van het nieuwe briefpapier wanneer je wilt printen.</p>\r\n<p>Bij vragen kun je terecht bij&nbsp;je eigen receptie of bij&nbsp;C&amp;M (Moniek Vrielink).</p>\r\n<p><em><strong>LET OP:</strong> bij contracten, aanbiedingsbrief, brief start werkzaamheden (zonder en met contract) en correspondentie moet je bij reeds bestaande documenten het oude briefpapier gebruiken. Deze documenten staan qua opmaak nog in de oude stijl en worden NIET omgezet.&nbsp;</em></p>\r\n<!--break-->', '<p><img alt=\"\" src=\"/sites/default/files/file/nieuws/IMG_4444_0.jpg\" />In navolging&nbsp;van de nieuwe huisstjil voor de visitekaartjes, gaan we morgen (vrijdag 15 augustus) ook over naar nieuw briefpapier en enveloppen.&nbsp;</p>\r\n<p>De gele balk komt nu overal terug en we nemen &quot;afscheid&quot; van de linkerkantlijn op het&nbsp;papier.&nbsp;</p>\r\n<p>Alle&nbsp;vestigingen&nbsp;zijn inmiddels voorzien van het&nbsp;nieuwe papier en de enveloppen en morgen worden officieel de sjablonen in Lotus Notes veranderd&nbsp;in de nieuwe stijl.</p>\r\n<p>Vanaf morgen zul je dus&nbsp;gebruik moeten maken van het nieuwe briefpapier wanneer je wilt printen.</p>\r\n<p>Bij vragen kun je terecht bij&nbsp;je eigen receptie of bij&nbsp;C&amp;M (Moniek Vrielink).</p>\r\n<p><em><strong>LET OP:</strong> bij contracten, aanbiedingsbrief, brief start werkzaamheden (zonder en met contract) en correspondentie moet je bij reeds bestaande documenten het oude briefpapier gebruiken. Deze documenten staan qua opmaak nog in de oude stijl en worden NIET omgezet.&nbsp;</em></p>\r\n', '', '1408087528', '2');
INSERT INTO `node_revisions` VALUES ('3599', '3601', '340', 'Zoekfunctie in project browser', '<p>In 2013 is in de projectbrowser een zoekfunctie&nbsp;toegevoegd.</p>\r\n<p>Als je in de projectbrowser&nbsp;op je rechtermuisknop&nbsp;klikt&nbsp;op &eacute;&eacute;n van de views staat&nbsp;onderaan&nbsp;&quot;Search....&quot; en als je dit doet bij&nbsp;de families of groups staat er alleen&nbsp;&quot;Search...&quot;.</p>\r\n<p>Vervolgens kom je in een venstertje waarin je kan&nbsp;zoeken. Dit venstertje is interactief want je kan pas op next klikken (deze&nbsp;is eerst grijs) als&nbsp;hetgene wat&nbsp;je intikt&nbsp;ook daadwerkelijk&nbsp;in je browser voorkomt.</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/gebruiker/340/Capture2.JPG\" style=\"width: 296px; height: 167px;\" /><img alt=\"\" src=\"/sites/default/files/gebruiker/340/Capture.JPG\" style=\"width: 290px; height: 167px;\" /></p>\r\n<p>Mark Bothof</p>\r\n', '<p>In 2013 is in de projectbrowser een zoekfunctie&nbsp;toegevoegd.</p>\r\n<p>Als je in de projectbrowser&nbsp;op je rechtermuisknop&nbsp;klikt&nbsp;op &eacute;&eacute;n van de views staat&nbsp;onderaan&nbsp;&quot;Search....&quot; en als je dit doet bij&nbsp;de families of groups staat er alleen&nbsp;&quot;Search...&quot;.</p>', '', '1364477705', '2');
