INSERT INTO `node_revisions` VALUES ('975', '976', '0', 'Taakverdeling Machteld Schoeman - Eveline der Nederlanden ', '<p>Tijdens het zwangerschapsverlof van Machteld Schoeman (directiesecretaresse) heeft Eveline der Nederlanden haar taken waargenomen. Zij zal dit definitief blijven doen voor een aantal zaken.&nbsp;</p>\r\n<p><span>Machteld Schoeman zal in de functie van directiesecretaresse ondersteuning geven op administratief, secretarieel en organisatorisch gebied aan de directieleden. Daarnaast zal zij ook ondersteuning bieden aan de verschillende afdelingen van Engineering en het Ingenieursbureau. </span></p>\r\n<p><span>Eveline der Nederlanden, zal alle facilitaire zaken oppakken in de functie van administratief medewerkster. Zij is het aanspreekpunt voor de organisatie.</span></p>\r\n<p><b><span>Werkdagen Machteld</span></b></p>\r\n<ul>\r\n    <li><span>Maandag, woensdag en donderdag (op kantoor)</span></li>\r\n    <li>Dinsdag en vrijdag mobiel bereikbaar voor noodgevallen</li>\r\n</ul>\r\n<div><b><span>Werkdagen Eveline</span></b></div>\r\n<ul>\r\n    <li><span>Maandagmorgen, dinsdag, woensdag, donderdag en vrijdagmorgen</span></li>\r\n</ul>\r\n<div><span>Beheer facilitaire zaken zijn o.a.:</span></div>\r\n<ul>\r\n    <li><span>Kopieermachines&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></li>\r\n    <li><span>Frankeermachines&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></li>\r\n    <li><span>Koffiezetapparaten</span></li>\r\n    <li><span>Mobiele telefoon</span></li>\r\n    <li><span><span style=\"font: 7pt &quot;Times New Roman&quot;;\">&nbsp;</span></span><span>Wagenpark</span></li>\r\n    <li><span>Autoverzekering / Schade afhandeling</span></li>\r\n    <li><span>Facilitaire meldingen van het kantoorpand in Apeldoorn</span></li>\r\n</ul>\r\n<div style=\"margin-left: 36pt; text-indent: -18pt;\">&nbsp;</div>\r\n<div style=\"margin-left: 36pt; text-indent: -18pt;\">&nbsp;</div>\r\n<p>&nbsp;</p>', '<p>Tijdens het zwangerschapsverlof van Machteld Schoeman (directiesecretaresse) heeft Eveline der Nederlanden haar taken waargenomen. Zij zal dit definitief blijven doen voor een aantal zaken.&nbsp;</p>', '', '1314783362', '2');
