INSERT INTO `node_revisions` VALUES ('202', '202', '32', 'Verslagen', '<p>Hieronder staan de meest recente verslagen van het OV&nbsp;en OR overleg. De oudere verslagen kun je opvragen via het OR lid van je vestiging.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Ondernemingsraad Overlegvergadering (OV):</strong></p>\r\n<p><a href=\"/sites/default/files/Besluitenlijst OV101.pdf\">Besluitenlijst OV 101</a></p>\r\n<p><a href=\"/sites/default/files/Besluitenlijst OV102.pdf\">Besluitenlijst OV 102</a></p>\r\n<p><a href=\"/sites/default/files/file/afdelingen/OR/Notulen%20Overlegvergadering%20OV103%20dd%2018%20december%202014.pdf_OpenElement%26FileName%3DNotulen%20Overlegvergadering%20OV103%20dd%2018%20december%202014.pdf\">Verslag OV 103</a></p>\r\n<p><a href=\"/sites/default/files/file/afdelingen/OR/Notulen%20Overlegvergadering%20OV104%20dd%2026%20februari%202015.pdf\">Verslag OV 104</a></p>\r\n<p><a href=\"/sites/default/files/file/afdelingen/OR/Notulen%20Overlegvergadering%20OV105%20dd%2021%20mei%202015.pdf\">Verslag OV 105</a></p>\r\n<p><a href=\"/sites/default/files/file/afdelingen/OR/Notulen%20Overlegvergadering%20OV106%20dd%2001%20oktober%202015.pdf\">Verslag OV 106</a></p>\r\n<p><a href=\"/sites/default/files/file/nieuws/Notulen%20Overlegvergadering%20OV107%20dd%2017%20december%202015.pdf\">Verslag OV 107</a></p>\r\n<p><a href=\"/sites/default/files/file/afdelingen/OR/OV109%20verslag%2030%20junil%202016_getekend_DEF.pdf\">Verslag OV 109</a></p>\r\n<p><strong>Ondernemingsraad overleg (OR):</strong></p>\r\n<p><a href=\"/sites/default/files/file/afdelingen/OR/verslag_OR126_22_04_2015_DEF.pdf_OpenElement%26FileName%3Dverslag_OR126_22_04_2015_DEF.pdf\">Verslag OR 126</a></p>\r\n<p><a href=\"/sites/default/files/file/afdelingen/OR/verslag_OR127_21%20mei%202015_DEF.pdf_OpenElement%26FileName%3Dverslag_OR127_21%20mei%202015_DEF.pdf\">Verslag OR 127</a></p>\r\n<p><a href=\"/sites/default/files/file/afdelingen/OR/verslag_OR128_02-07-2015_DEF.pdf_OpenElement%26FileName%3Dverslag_OR128_