INSERT INTO `node_revisions` VALUES ('3851', '3853', '178', 'Wapening in kleur?', '<p>Ik revit teken ik de wapening&nbsp;vaak nog met detail components. (Voor mij is dit een stuk snellen&nbsp;dat de rabar functie)</p>\r\n<p>Nu had ik bedacht om de wapening en de wapeningsteksten een kleurtje mee te geven om dit duidelijker te maken&nbsp;en de pdf toch vaal&nbsp;al kleur zijn. Ik heb gekozen voor een donderbruin&nbsp;zodat het niet te veel opvalt en nog wel te zien is bij zwart/wit afdruk. Ik ga het&nbsp;eens een projectje proberen...</p>\r\n<p>Ik ben nog wel&nbsp;benieuwd&nbsp;wat jullie vinden.</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/gebruiker/178/wap%20bruin.JPG\" style=\"width: 514px; height: 480px;\" /></p>\r\n', '<p>Ik revit teken ik de wapening&nbsp;vaak nog met detail components. (Voor mij is dit een stuk snellen&nbsp;dat de rabar functie)</p>', '', '1410252932', '2');
