INSERT INTO `node_revisions` VALUES ('592', '593', '1', 'Bartels Bouwtoezicht', '<p>&nbsp;<img height=\"128\" width=\"640\" src=\"/sites/default/files/gebruiker/240/logobbt.jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Zoals wellicht al bekend is, is eind 2008 Bartels Bouwtoezicht van start gegaan.&nbsp;</p>\r\n<p>Bartels Bouwtoezicht biedt kwaliteit ondersteunende diensten aan. Was vroeger de rol van de opzichter hoofdzakelijk neergelegd bij de ontwikkelaar of opdrachtgever, de laatste tijd zien we steeds vaker dat aannemers ervoor kiezen om onze ondersteuning in te schakelen. Gebleken is namelijk dat met name op grote projecten onze diensten tot een forse besparing op de faalkosten leiden.&nbsp;</p>\r\n<p>Wil je meer weten over onze diensten, neem dan contact op met:</p>\r\n<p>Ruud van der Breggen of Bert Heite</p>\r\n<p>De komende tijd zal ik maandelijks een voorbeeld van een bouwfout op dit forum plaatsen. Zo kan iedereen eens zien wat er buiten misgaat.</p>\r\n<p>Groeten en veel plezier op dit forum,</p>\r\n<p>Bert Heite</p>\r\n<p>Tel.: 06-12 987 094</p>\r\n<p>bheite@bartels.nl</p>\r\n<p>&nbsp;</p>', '<p>&nbsp;<img height=\"128\" width=\"640\" src=\"/sites/default/files/gebruiker/240/logobbt.jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Zoals wellicht al bekend is, is eind 2008 Bartels Bouwtoezicht van start gegaan.&nbsp;</p>', '', '1272872150', '2');
INSERT INTO `node_revisions` VALUES ('239', '240', '1', 'Gordingen', '<p>Voor gordingen in een RECHT dakvlak kun je een beam-systeem gebruiken die wordt getekend op een Ref-plane. Als ref-plane moet bijvoorbeeld de bovenzijde van een dakspant worden aangewezen. Het ref-plane mag dus schuin staan, maar kan niet rond zijn. Een groot voordeel is dat de tussenstand of dimensionering van de gordingen eenvoudig kan worden aangepast.</p>', '<p>Voor gordingen in een RECHT dakvlak kun je een beam-systeem gebruiken die wordt getekend op een Ref-plane. Als ref-plane moet bijvoorbeeld de bovenzijde van een dakspant worden aangewezen. Het ref-plane mag dus schuin staan, maar kan niet rond zijn. Een groot voordeel is dat de tussenstand of dimensionering van de gordingen eenvoudig kan worden aangepast.</p>', '', '1272874531', '2');
