INSERT INTO `node_revisions` VALUES ('3921', '3923', '32', 'Samenvoeging vestigingen Elst en Veenendaal', '<p>Sinds half maart zijn de vestigingen Elst en Veenendaal, na het vertrek van vestigingsdirecteur Ruud van der Breggen in Veenendaal, samengevoegd. Anton de Boer is tijdelijk&nbsp;vestigingsdirecteur&nbsp;voor de inmiddels samengevoegde vestigingen.</p>\r\n<p>De beide locaties&nbsp;zijn nu gevestigd in het pand van Elst, aan de Nijverheidsweg. Inmiddels mag dit ook officieel naar buiten toe gecommuniceerd worden en is de vestiging Veenendaal&nbsp;dus ook van onze website.&nbsp;</p>\r\n<p>Voor vragen hierover kun je terecht bij&nbsp;Anton de Boer of Pieter van Boom.&nbsp;</p>\r\n', '<p>Sinds half maart zijn de vestigingen Elst en Veenendaal, na het vertrek van vestigingsdirecteur Ruud van der Breggen in Veenendaal, samengevoegd. Anton de Boer is tijdelijk&nbsp;vestigingsdirecteur&nbsp;voor de inmiddels samengevoegde vestigingen.</p>', '', '1426668682', '2');
