INSERT INTO `node_revisions` VALUES ('3828', '3830', '178', 'Schoorpalen met trekbelasting?', '<p>Als het goed is werken we allemaal al wel met het nieuwe palenplansyteem.</p>\r\n<p>Bij palen met trek kan je in de type een vinkje&nbsp;aan zetten en er komt een mooi rode pijl (smaken verschillen) bij de paal te voorschijn.</p>\r\n<p>Palen die schoor&nbsp;geheid worden&nbsp;zijn voorzien van een&nbsp;pijl met de schoorstand. De kan de paal&nbsp;draaien afhankelijk van de schoorrichting. De paalsymbool&nbsp;moet je dan weer terug draaien&nbsp;zodat de symbolen op tekening het zelfde&nbsp;blijven staan onafhankelijk&nbsp;van de schoorrichting.</p>\r\n<p>Maar.... Ik heb nu schoorpalen MET een trekbelasting..... En de trekpijlen wijzen nu alle kanten op... iemand een&nbsp;idee?</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n', '<p>Als het goed is werken we allemaal al wel met het nieuwe palenplansyteem.</p>\r\n<p>Bij palen met trek kan je in de type een vinkje&nbsp;aan zetten en er komt een mooi rode pijl (smaken verschillen) bij de paal te voorschijn.</p>', '', '1401882303', '2');
