INSERT INTO `node_revisions` VALUES ('3169', '3170', '32', 'Werkzaamheden aan Station Europapark Groningen', '<p>&nbsp;Momenteel wordt er hard gewerkt aan de bouw van het nieuwe Station Europapark in Groningen.</p>\r\n<!--break-->\r\n<div>&nbsp;</div>\r\n<div>Van 14 februari tot en met 5 maart 2012, vinden er grootschalige werkzaamheden aan de tunnels plaats die een buitendienststelling van het spoor noodzakelijk maken.&nbsp;Volg de werkzaamheden aan het spoor live via de webcam van ProRail:&nbsp;<a target=\"_blank\" href=\"http://www.spoorwerkeuropapark.nl/\">www.spoorwerkeuropapark.nl</a></div>\r\n<div>&nbsp;</div>\r\n<div><a href=\"http://www.bartels-global.com/nl/projecten/station-europapark-groningen\">Lees hier alles over het project</a>, waar Bartels aan werkt als hoofd- en deelconstructeur.&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>Hieronder zie een je artist impression filmpje van hoe het station en de omgeving eruit gaan zien.&nbsp;</div>\r\n<p>&nbsp;<iframe width=\"420\" height=\"315\" src=\"http://www.youtube.com/embed/Ub2KM39yhTA\" frameborder=\"0\" allowfullscreen=\"\"></iframe></p>', '<p>&nbsp;Momenteel wordt er hard gewerkt aan de bouw van het nieuwe Station Europapark in Groningen.</p>\r\n', '', '1330082994', '2');
