INSERT INTO `node_revisions` VALUES ('979', '980', '1', 'Werkdagen Carolien Garritsen (F&A)', '<p>De werkdagen van Carolien Garritsen (F&amp;A) zijn met ingang van volgende week veranderd. Zij zal nu op kantoor aanwezig zijn op Maandag, Dinsdag en Donderdag. Voor al je vragen over bijvoorbeeld de salarisadminstratie kun je bij haar terecht.</p>\r\n<p>Voor meer informatie over de afdeling F&amp;A, werkdagen, taakverdeling etc. kun je kijken onder <a href=\"/afdelingen/fa\">Afdelingen &gt; F&amp;A</a>.&nbsp;</p>', '<p>De werkdagen van Carolien Garritsen (F&amp;A) zijn met ingang van volgende week veranderd. Zij zal nu op kantoor aanwezig zijn op Maandag, Dinsdag en Donderdag. Voor al je vragen over bijvoorbeeld de salarisadminstratie kun je bij haar terecht.</p>', '', '1314949681', '2');
INSERT INTO `node_revisions` VALUES ('980', '981', '1', 'Verjaardagsfeestje Bartels - De Locatie!', '<p>Het was bij de tweede hint al geraden (chapeau voor Jaco Bos en Allerd Hobers), de feestlocatie van 1 oktober is: Fort Pampus. Het eiland in het IJ.</p>\r\n<!--break-->\r\n<p>Vanaf de tuinen van het Muiderslot zullen we afvaren naar het eiland Pampus, waar we in de Fortgracht ons feestje gaan vieren!</p>\r\n<p><img width=\"600\" height=\"400\" alt=\"\" src=\"/sites/default/files/file/Hint5_0.jpg\" /></p>\r\n<p>Vanaf maandag kun je je intekenen bij de receptie op je eigen vestiging. Ook ontvang je maandag in je mail de offici&euml;le uitnodiging met alle informatie (tijden, exacte locatie, vervoer, etc.). <strong>Tot 1 oktober!&nbsp;</strong></p>', '<p>Het was bij de tweede hint al geraden (chapeau voor Jaco Bos en Allerd Hobers), de feestlocatie van 1 oktober is: Fort Pampus. Het eiland in het IJ.</p>\r\n', '', '1314950075', '2');
