INSERT INTO `node_revisions` VALUES ('802', '803', '32', 'Handleiding Risicomanagement', '<p>De afdeling Kwaliteit heeft een handleiding opgesteld voor het gebruik van Risicomanagement in Lotus Notes (OrganisationWare).</p>\r\n<p>De handleiding vind je onder <a href=\"/afdelingen/kwaliteit/risicomanagement\">Afdelingen &gt;&nbsp;Kwaliteit &gt;&nbsp;Risicomanagement. </a></p>', '<p>De afdeling Kwaliteit heeft een handleiding opgesteld voor het gebruik van Risicomanagement in Lotus Notes (OrganisationWare).</p>\r\n<p>De handleiding vind je onder <a href=\"/afdelingen/kwaliteit/risicomanagement\">Afdelingen &gt;&nbsp;Kwaliteit &gt;&nbsp;Risicomanagement. </a></p>', '', '1297268094', '2');
INSERT INTO `node_revisions` VALUES ('778', '779', '32', 'Risicomanagement op infonet', '<p>Eind 2010 is een begin gemaakt met het managen van de risico\'s op onze projecten door middel van de Workshop Risicomanagement. Hierover heb je ook kunnen lezen in de Binnen Bartels van December. <a href=\"/sites/default/files/file/afdelingen/kwaliteit/Binnen%20Bartels%20-%20Risicomanagement.pdf\">Lees dat artikel hier</a>. </p>\r\n<p>Op Infonet wordt er gedurende het jaar meer informatie verstrekt over Risicomanagement, waaronder standaard documentatie. Dit is te vinden onder <a href=\"/afdelingen/kwaliteit/risicomanagement\">Afdelingen &gt; Kwaliteit &gt;&nbsp;Risicomanagement.</a> </p>\r\n<p>Voor vragen hierover kun je altijd terecht bij <a href=\"/users/foppe-haasnoot\">Foppe Haasnoot</a>. </p>', '<p>Eind 2010 is een begin gemaakt met het managen van de risico\'s op onze projecten door middel van de Workshop Risicomanagement. Hierover heb je ook kunnen lezen in de Binnen Bartels van December. <a href=\"/sites/default/files/file/afdelingen/kwaliteit/Binnen%20Bartels%20-%20Risicomanagement.pdf\">Lees dat artikel hier</a>. </p>', '', '1294934511', '2');
