INSERT INTO `node_revisions` VALUES ('1089', '1090', '32', 'Presentatie COBc/CUR-dag', '<p>Afgelopen donderdag 10 november was Pieter van Boom uitgenodigd op de COBc/CUR-dag om een presentatie te houden over de veiligheid van de constructies en de controle van een constructeur op zijn eigen werkzaamheden in de uitvoeringsfase.</p>\r\n<p>Tijdens deze dag werd verder ingegaan op&nbsp;de ontwikkelingen in het bouwtoezicht ( zoals o.a. de RUD\'s , vergunningsvrij bouwen en gecertificeerd bouwplan toetsen), de vragen die deze ontwikkelingen oproepen en over wat het COBc daaraan zou kunnen doen.</p>\r\n<p>Vanuit Bartels hebben vele mensen bijgedragen aan de inhoud van deze presentatie, waaronder Bert Heite, Eugene Glorie, Wim Looman, Andree de Witte, Ron Massop, Ruud van der Breggen, Harold Fidder en Ren&eacute; van Brandenburg.</p>\r\n<p>Hieronder kun je de pdf van de presentatie downloaden.&nbsp;</p>', '<p>Afgelopen donderdag 10 november was Pieter van Boom uitgenodigd op de COBc/CUR-dag om een presentatie te houden over de veiligheid van de constructies en de controle van een constructeur op zijn eigen werkzaamheden in de uitvoeringsfase.</p>', '', '1321008411', '2');
