INSERT INTO `node_revisions` VALUES ('1025', '1026', '0', 'Central files terugzetten naar local files kan in 2012', '<p><span class=\"Apple-style-span\" style=\"text-align: left; widows: 2; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; font: 11px Tahoma, Arial, Verdana; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(102,102,102); word-spacing: 0px; -webkit-text-decorations-in-effect: none; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">\r\n<ol>\r\n    <li>Gebruik het &nbsp;Open Project commando</li>\r\n    <li>Zoek je Local file,</li>\r\n    <li>Highlighten</li>\r\n    <li>Selecteer&nbsp; &quot;Detach from Central&quot; Worksharing Option,</li>\r\n    <li>Selecteer Open,</li>\r\n    <li>Kies&nbsp;&quot;Detach and discard worksets&quot;</li>\r\n    <li>Save File (Worksets zijn verdwenen)</li>\r\n</ol>\r\n</span></p>', '<p><span class=\"Apple-style-span\" style=\"text-align: left; widows: 2; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; font: 11px Tahoma, Arial, Verdana; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(102,102,102); word-spacing: 0px; -webkit-text-decorations-in-effect: none; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">\r\n<ol>\r', '', '1317111685', '2');
