INSERT INTO `node_revisions` VALUES ('1090', '1091', '144', 'staal family opgeslagen in revit 2012', '<p>Ik kan een staal strip family niet laden omdat deze is opgeslagen in een latere versie dan 2011.</p>\r\n<p>Ik heb het vermoeden dat dit dan 2012 is, moet elke versie zijn eigen familyfile krijgen? En wie wil die strip even terugzetten voor mij naar 2011?</p>\r\n<p>Kan dat uberhaupt nog wel?</p>\r\n<p><img width=\"640\" height=\"413\" alt=\"\" src=\"/sites/default/files/gebruiker/144/family6.PNG\" /></p>\r\n<p>Allerd</p>\r\n<p>&nbsp;</p>', '<p>Ik kan een staal strip family niet laden omdat deze is opgeslagen in een latere versie dan 2011.</p>\r\n<p>Ik heb het vermoeden dat dit dan 2012 is, moet elke versie zijn eigen familyfile krijgen? En wie wil die strip even terugzetten voor mij naar 2011?</p>\r\n<p>Kan dat uberhaupt nog wel?</p>\r\n<p><img width=\"640\" height=\"413\" alt=\"\" src=\"/sites/default/files/gebruiker/144/family6.PNG\" /></p>', '', '1321010929', '2');
INSERT INTO `node_revisions` VALUES ('1091', '1092', '32', 'Presentatie Bouwkunde Seminar', '<p>Donderdag 10 november organiseerde Bartels samen met een aantal andere marktpartijen een Bouwkunde Seminar op gebied van Innovatie, Onderhoud en Herstel. Tijdens deze middag werden ge&iuml;nteresseerden ingelicht over innovatietrends in de woning- en utiliteitsbouw.</p>\r\n<p>Naast een presentatie over de isolatiewaarde van je gevel verhogen door Gerard Hol, hadden we hier ook een stand staan. Jan Willems en Gertie Snip vertegenwoordigden Bartels bij onze stand.&nbsp;</p>\r\n<p>Hieronder kun je de hele presentatie die Gerard Hol hield in z\'n geheel bekijken.&nbsp;</p>\r\n<p>Meer informatie over de Bouwkunde Seminar vind je op de website,&nbsp;<a href=\"http://www.bouwkundeseminars.nl/\">http://www.bouwkundeseminars.nl/</a></p>', '<p>Donderdag 10 november organiseerde Bartels samen met een aantal andere marktpartijen een Bouwkunde Seminar op gebied van Innovatie, Onderhoud en Herstel. Tijdens deze middag werden ge&iuml;nteresseerden ingelicht over innovatietrends in de woning- en utiliteitsbouw.</p>', '', '1321011240', '2');
