INSERT INTO `node_revisions` VALUES ('2621', '2622', '32', 'Aanpassing Leasereglement', '<p>In verband met de reorganisatie zullen in 2012 ook een groot aantal besparingen worden doorgevoerd binnen Bartels, waaronder een aanpassing van het leasereglement. Er worden scherpere regels gesteld en milieuvriendelijkere auto\'s worden gestimuleerd.</p>\r\n<p>Het nieuwe reglement is te vinden hier op infonet, onder <a href=\"/afdelingen/po/documenten\">Afdelingen &gt; P&amp;O &gt; Documenten</a>. Medewerkers die momenteel leasen worden middels een persoonlijke brief op de hoogte gesteld van de veranderingen.</p>\r\n<p>Voor vragen over het nieuwe leasereglement kun je terecht bij <a href=\"/users/machteld-schoeman\">Machteld Schoeman</a> of <a href=\"/users/frank-van-overbeeke\">Frank van Overbeeke</a>.&nbsp;</p>\r\n<!--break-->\r\n', '<p>In verband met de reorganisatie zullen in 2012 ook een groot aantal besparingen worden doorgevoerd binnen Bartels, waaronder een aanpassing van het leasereglement. Er worden scherpere regels gesteld en milieuvriendelijkere auto\'s worden gestimuleerd.</p>\r\n<p>Het nieuwe reglement is te vinden hier op infonet, onder <a href=\"/afdelingen/po/documenten\">Afdelingen &gt; P&amp;O &gt; Documenten</a>. Medewerkers die momenteel leasen worden middels een persoonlijke brief op de hoogte gesteld van de veranderingen.</p>\r\n<p>Voor vragen over het nieuwe leasereglement kun je terecht bij <a href=\"/users/machteld-schoeman\">Machteld Schoeman</a> of <a href=\"/users/frank-van-overbeeke\">Frank van Overbeeke</a>.&nbsp;</p>\r\n', '', '1325768046', '2');
INSERT INTO `node_revisions` VALUES ('2622', '2623', '32', 'Spaarloonregeling', '<div>Zoals bekend is vanaf 1 januari 2012 de spaarloonregeling komen te vervallen en kunnen alle saldi vrij worden opgenomen. Daarvan hebben jullie van de ASR-bank bericht gehad.</div>\r\n<div>&nbsp;</div>\r\n<div>Nu staat er in die brief en op de site van de ASR-bank dat er bij opname fiscale consequenties van toepassing kunnen zijn. Ik heb dit nagevraagd en die consequenties betreffen alleen als het opnamebedrag de &euro; 17.000,00 overtreft. Dit zal mijns inziens niet of nauwelijks voorkomen.&nbsp;Conclusie: de spaarbedragen zijn vrij over te boeken.&nbsp;Vragen? Neem contact met me op!</div>\r\n<div>&nbsp;</div>\r\n<div><a href=\"http://users/katinka-wolters\">Katinka Wolters</a></div>\r\n<!--break-->\r\n', '<div>Zoals bekend is vanaf 1 januari 2012 de spaarloonregeling komen te vervallen en kunnen alle saldi vrij worden opgenomen. Daarvan hebben jullie van de ASR-bank bericht gehad.</div>\r\n<div>&nbsp;</div>\r\n<div>Nu staat er in die brief en op de site van de ASR-bank dat er bij opname fiscale consequenties van toepassing kunnen zijn. Ik heb dit nagevraagd en die consequenties betreffen alleen als het opnamebedrag de &euro; 17.000,00 overtreft. Dit zal mijns inziens niet of nauwelijks voorkomen.&nbsp;Conclusie: de spaarbedragen zijn vrij over te boeken.&nbsp;Vragen? Neem contact met me op!</div>\r\n<div>&nbsp;</div>\r\n<div><a href=\"http://users/katinka-wolters\">Katinka Wolters</a></div>\r\n', '', '1326103634', '2');
