INSERT INTO `node_revisions` VALUES ('3061', '3062', '32', 'Temporary dimensions voor dimensions?', '<p><span style=\"font-family: Arial; font-size: 9pt\">Om maatvoering op een vaste afstand van je reference&nbsp;te krijgen, kun je bij AutoCAD een afstand defini&euml;ren (shirt + rechtermuisknop). Nu wil ik ook met Revit een bepaalde afstand tussen de maatvoering en de instance (en een bepaalde afstand tussen de dimensions onderling). Misschien temporary dimensions als je de dimensions plaatst? <o:p></o:p></span></p>\r\n<p><span style=\"font-family: Arial; font-size: 9pt\">Hoe krijgen jullie dit voor elkaar? <o:p></o:p></span></p>\r\n<p>&nbsp;Steffen Walvius</p>', '<p><span style=\"font-family: Arial; font-size: 9pt\">Om maatvoering op een vaste afstand van je reference&nbsp;te krijgen, kun je bij AutoCAD een afstand defini&euml;ren (shirt + rechtermuisknop). Nu wil ik ook met Revit een bepaalde afstand tussen de maatvoering en de instance (en een bepaalde afstand tussen de dimensions onderling). Misschien temporary dimensions als je de dimensions plaatst?', '', '1326723820', '2');
INSERT INTO `node_revisions` VALUES ('3067', '3068', '32', 'Stadsplein Forum in Tilburg', '<p>In Tilburg wordt gebouwd aan een grootstedelijke ontmoetingsplek direct naast treinstation Tilburg Reeshof.&nbsp;</p>\r\n<!--break-->\r\n<p>Het Stadsplein Forum herbergt straks woningen, winkels en horeca in drie bouwdelen, die zich bevinden rondom een modern plein.</p>\r\n<p>Bartels Eindhoven is als hoofdconstructeur betrokken bij dit project en verzorgt in die hoedanigheid het constructief ontwerp. Daarnaast heeft Bartels ook geotechnisch advies geleverd.&nbsp;</p>\r\n<p>Meer informatie is te vinden op <a target=\"_blank\" href=\"http://www.stadspleinforum.nl/plan/\">de website van het Stadsplein</a>. Bekijk hieronder de 3D animatie van het te bouwen complex!</p>\r\n<p><iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/mRK21PZz7P4\" frameborder=\"0\" allowfullscreen=\"\"></iframe></p>', '<p>In Tilburg wordt gebouwd aan een grootstedelijke ontmoetingsplek direct naast treinstation Tilburg Reeshof.&nbsp;</p>\r\n', '', '1326811923', '2');
