INSERT INTO `node_revisions` VALUES ('3071', '3072', '136', 'kip- en kniksteun door wandregel', '<p>Onlangs aangetroffen bij een bezoek aan een hal.</p>\r\n<p>De verbinding tussen wandregel en kolom was aan beide zijden uitgevoerd met slobgaten. Zo past het natuurlijk altijd, maar de constructieve functie als kip- en kniksteun van de zeer slanke kolom (IPE240, 12,5 m hoog) is uiteraard niet mogelijk, behalve door een beetje wrijving tussen de platen..</p>\r\n<p>Daarnaast valt op dat het schotje wel tegen de buitenste flens is gelast, maar niet tegen de binnenste. In het geval van windzuiging (als dit de&nbsp;zijgevel is), is de factor echter ook&nbsp;0,8, zodat beide flenzen even zwaar&nbsp;belast kunnen worden.&nbsp;De Eurocode biedt straks wel de mogelijkheid om voor de kolommen in het midden van een gevel met 0,5 te rekenen ipv 0,8.&nbsp;</p>', '<p>Onlangs aangetroffen bij een bezoek aan een hal.</p>\r\n<p>De verbinding tussen wandregel en kolom was aan beide zijden uitgevoerd met slobgaten. Zo past het natuurlijk altijd, maar de constructieve functie als kip- en kniksteun van de zeer slanke kolom (IPE240, 12,5 m hoog) is uiteraard niet mogelijk, behalve door een beetje wrijving tussen de platen..</p>', '', '1327314957', '2');
