INSERT INTO `node_revisions` VALUES ('3072', '3073', '144', 'Kopieren van Sneden tbv wapening', '<p>Ik heb een snede die bestaat uit een Revit model van een betonstrook en aan weerszijden daarvan een gelinkt revitmodel.</p>\r\n<p>Voor de vorm werkt dit goed. Ik kan de gelinkte modellen via de graphic override \'halftone\'weergeven waardoor goed verschil zichtbaar is.</p>\r\n<p>Wanneer ik de snede duplicate with detailling gebeuren er&nbsp; 2 dingen die ik niet wil.</p>\r\n<p>1: Hij geeft de als \'halftone\' ingestelde links wederom weer als normaal.</p>\r\n<p>2: Hij geeft de ook de wapening weer van de gelinkte modellen</p>\r\n<p>&nbsp;</p>\r\n<p>Heeft iemand een oplossing voor (1) het handhaven van de grapfic overrides bij het kopieren van een snede en (2) voor het niet zichtbaar maken van de wapening van gelinkte modellen in een view waar je wel het model wilt wapenen? Hoe doen jullie dit normaal?</p>\r\n<p>Allerd</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" width=\"640\" height=\"479\" src=\"/sites/default/files/gebruiker/144/wapening5.PNG\" /></p>', '<p>Ik heb een snede die bestaat uit een Revit model van een betonstrook en aan weerszijden daarvan een gelinkt revitmodel.</p>\r\n<p>Voor de vorm werkt dit goed. Ik kan de gelinkte modellen via de graphic override \'halftone\'weergeven waardoor goed verschil zichtbaar is.</p>\r\n<p>Wanneer ik de snede duplicate with detailling gebeuren er&nbsp; 2 dingen die ik niet wil.</p>', '', '1327321751', '2');
