INSERT INTO `node_revisions` VALUES ('3070', '3071', '0', 'Nummeren van palen begint niet met nr. 1', '<p>Via \'Element Positioning\' probeer ik een palenplan te nummeren. Deze loopt door zonder foutmeldingen. Het vreemde is alleen dat de nummering begint met nr. 1231 en niet met nr. 1.</p>\r\n<p>In&nbsp;het project hebben wel ooit ongeveer 1230 extra palen ingestaan. Maar deze zijn er nu uitgehaald. Wordt er misschien toch nog ergens een instelling vast gehouden?</p>\r\n<p>Heeft iemand een idee?</p>\r\n<p>Groeten Ellbert</p>\r\n<p>&nbsp;</p>', '<p>Via \'Element Positioning\' probeer ik een palenplan te nummeren. Deze loopt door zonder foutmeldingen. Het vreemde is alleen dat de nummering begint met nr. 1231 en niet met nr. 1.</p>\r\n<p>In&nbsp;het project hebben wel ooit ongeveer 1230 extra palen ingestaan. Maar deze zijn er nu uitgehaald. Wordt er misschien toch nog ergens een instelling vast gehouden?</p>\r\n<p>Heeft iemand een idee?</p>', '', '1327306720', '2');
