INSERT INTO `node_revisions` VALUES ('3151', '3152', '32', 'Bezoeken F&A en Kwaliteit op de vestiging', '<p>De vestigingsbezoeken die maandelijks worden gedaan door de afdelingen F&amp;A (Ronald Arentsen) en Kwaliteit (Foppe Haasnoot) staan inmiddels ook in de Agenda op Infonet. Hou deze bezoeken even in de gaten (zet het in de vestigingsagenda) en zorg dat er voldoende projectleiders aanwezig zijn om hen te woord te staan. De eerste bezoeken zullen in Maart plaatsvinden.&nbsp;</p>\r\n<p>Alvast bedankt!</p>\r\n<p><a href=\"/users/ronald-arentsen\">Ronald Arentsen</a> en <a href=\"/users/foppe-haasnoot\">Foppe Haasnoot</a>.&nbsp;</p>', '<p>De vestigingsbezoeken die maandelijks worden gedaan door de afdelingen F&amp;A (Ronald Arentsen) en Kwaliteit (Foppe Haasnoot) staan inmiddels ook in de Agenda op Infonet. Hou deze bezoeken even in de gaten (zet het in de vestigingsagenda) en zorg dat er voldoende projectleiders aanwezig zijn om hen te woord te staan. De eerste bezoeken zullen in Maart plaatsvinden.&nbsp;</p>', '', '1329302079', '2');
