INSERT INTO `node_revisions` VALUES ('3175', '3176', '178', 'gebruikt default view template', '<p>&nbsp;Voor de doorgewinterde view template gebruiker ...</p>\r\n<p>Als je voor het eerst een view template toepast zal de view deze template zien als default. Nu kan je makkelijk in de browser meerdere views tegelijk de view template verversen met de &quot;apply default view template&quot;.</p>\r\n<p>Maar let op, als je een <strong>andere</strong> view template kiest en toepast zal de default <strong>niet</strong> wijzigen ! &nbsp;</p>\r\n<p>Mijn idee om de volgende manier van werken aan te houden om er voor te zorgen dat het altijd goed gaat: Gebruik nooit meer gewoon apply view template, maar wijzig eerst de default view template in de view properties en vervolgens apply default view template.</p>\r\n<p>&nbsp;</p>\r\n<p>Eelco Dijkstra</p>', '<p>&nbsp;Voor de doorgewinterde view template gebruiker ...</p>\r\n<p>Als je voor het eerst een view template toepast zal de view deze template zien als default. Nu kan je makkelijk in de browser meerdere views tegelijk de view template verversen met de &quot;apply default view template&quot;.</p>', '', '1330426579', '2');
