INSERT INTO `node_revisions` VALUES ('3237', '3238', '178', 'Pin', '<p>&nbsp;Als revit gebruiker ken je het vast wel. Je wilt een wand verschuiven en andere wanden schuiven onbedoeld mee.... undo....</p>\r\n<p>Als je alleen die ene wand wilt moven kan je altijd disjoin gebruiken, maar dan vervalt alle maatvoeringen, koppelingen, tags, etc. niet handig.</p>\r\n<p>Ik heb zojuist het beste resultaat gehad met PIN. Ik heb eerst de omliggenden gepind (vast gezet) zodat deze niet verschuiven, en vervolgens die ene wand verschoven. En waarrempel, het werkte!</p>\r\n<p>Eelco</p>', '<p>&nbsp;Als revit gebruiker ken je het vast wel. Je wilt een wand verschuiven en andere wanden schuiven onbedoeld mee.... undo....</p>\r\n<p>Als je alleen die ene wand wilt moven kan je altijd disjoin gebruiken, maar dan vervalt alle maatvoeringen, koppelingen, tags, etc. niet handig.</p>', '', '1337086178', '2');
