INSERT INTO `node_revisions` VALUES ('3263', '3264', '32', 'Het grote Binnen Bartels tourspel', '<p>Het is weer tijd voor het jaarlijkse \'Grote Binnen Bartels Tourspel\', want de start van de Tour de France komt er weer aan! Komende zaterdag staan er 18 Nederlanders aan de start, het grootste aantal sinds 1991!&nbsp;</p>\r\n<!--break-->\r\n<p>Zou dit een voorbode zijn voor ons eigen Binnen Bartels Tourspel? Afgelopen jaar waren er 88 deelnemers, hoeveel worden dat er dit jaar?</p>\r\n<p>Hieronder kun je de spelregels en het invulformulier downloaden. Het ingevulde formulier kun je mailen naar <a href=\"mailto:smulder@bartels.nl\">Sven Mulder</a>!</p>\r\n<p>We hopen op een recordaantal deelnemers!!</p>\r\n<p><img src=\"http://www.vincentabry.com/en/wp-content/uploads/2011/10/2012-Tour-de-France-stages.jpg\" alt=\"\" /></p>', '<p>Het is weer tijd voor het jaarlijkse \'Grote Binnen Bartels Tourspel\', want de start van de Tour de France komt er weer aan! Komende zaterdag staan er 18 Nederlanders aan de start, het grootste aantal sinds 1991!&nbsp;</p>\r\n', '', '1347881022', '2');
INSERT INTO `node_revisions` VALUES ('3265', '3266', '32', 'Decos Noordwijk in Bouwen met Staal', '<p>In Noordwijk is een bijzonder kantoor gebouwd voor IT bedrijf Decos. Het kantoor lijkt op een neergestorte meteoriet. In Bouwen met Staal staat een uitgebreid artikel van 4 pagina\'s, geschreven door de architect van Inbo en door Hans Berkhout, projectleider van Bartels Veenendaal.</p>\r\n<p>Download hieronder het artikel.&nbsp;</p>', '<p>In Noordwijk is een bijzonder kantoor gebouwd voor IT bedrijf Decos. Het kantoor lijkt op een neergestorte meteoriet. In Bouwen met Staal staat een uitgebreid artikel van 4 pagina\'s, geschreven door de architect van Inbo en door Hans Berkhout, projectleider van Bartels Veenendaal.</p>\r\n<p>Download hieronder het artikel.&nbsp;</p>', '', '1340878511', '2');
