INSERT INTO `node_revisions` VALUES ('3302', '3304', '32', 'Bartels zaalvoebaltoernooi', '<p>Aanstaande vrijdag is het weer zover, het jaarlijkse Bartels zaalvoetbal toernooi! Wederom wordt er in sporthal Valkenhuizen in Arnhem gestreden om de felbegeerde winst. Om 18.30 start het toernooi!</p>\r\n<p>Naast spelers zijn supporters natuurlijk ook van harte welkom!! Dus kom je team/vestiging aanmoedigen. En na afloop is er ook altijd even een gezellige borrel in de kantine bij de sporthal.</p>\r\n<p>Voor verdere vragen kun je terecht bij de organisator, <a href=\"mailto:phellinga@bartels.nl\">Perseus Hellinga</a> (Leeuwarden).</p>\r\n<p>Sporthal Valkenhuizen<br />\r\nBeukenlaan 15<br />\r\nArnhem</p>', '<p>Aanstaande vrijdag is het weer zover, het jaarlijkse Bartels zaalvoetbal toernooi! Wederom wordt er in sporthal Valkenhuizen in Arnhem gestreden om de felbegeerde winst. Om 18.30 start het toernooi!</p>', '', '1347270557', '2');
