INSERT INTO `node_revisions` VALUES ('3366', '3368', '32', 'Handelsdag 2012 op 27 november a.s.', '<p>Dit jaar zal de Handelsdag 2012 worden gehouden op 27 november a.s.. Het uitstel van de Handelsdag dit jaar had te maken met de financi&euml;le situatie van Bartels Engineering.&nbsp;Zoals reeds bekend, is de waarde van een certificaat van aandeel vastgesteld op &euro; 5,85.&nbsp;</p>\r\n<p>Dit jaar&nbsp;worden de brieven met betrekking&nbsp;tot koop en verkoop van certificaten niet per post verzonden, maar kun je het formulier hieronder downloaden. Print het formulier, vul het in en stuur het voor 20 november naar Carolien Garritsen (<a href=\"mailto:cgarritsen@bartels.nl\">cgarritsen@bartels.nl</a>).</p>\r\n<p>Voor&nbsp;inhoudelijke vragen over de Handelsdag of de certificaten kun je terecht&nbsp;bij Frank van Overbeeke of Taco Klevering.&nbsp;</p>\r\n', '<p>Dit jaar zal de Handelsdag 2012 worden gehouden op 27 november a.s.. Het uitstel van de Handelsdag dit jaar had te maken met de financi&euml;le situatie van Bartels Engineering.&nbsp;Zoals reeds bekend, is de waarde van een certificaat van aandeel vastgesteld op &euro; 5,85.&nbsp;</p>', '', '1352444528', '2');
