INSERT INTO `node_revisions` VALUES ('3415', '3417', '178', 'copy tags werkt niet, help?', '<p>Normaal&nbsp;gaat het redelijk goed als je een tag van een kolom of ligger copieerd&nbsp;naar een ander&nbsp;kolom of ligger, ook naar een andere view.<br />\r\n	Maar&nbsp;nu gooit revit in mijn&nbsp;project de tag gelijk weg als ik deze wil copieeren. Het&nbsp;project is gedeeld. Heeft iemand een oplossing?</p>\r\n<p>alvast bedankt, &nbsp;Eelco</p>\r\n', '<p>Normaal&nbsp;gaat het redelijk goed als je een tag van een kolom of ligger copieerd&nbsp;naar een ander&nbsp;kolom of ligger, ook naar een andere view.<br />\r\n	Maar&nbsp;nu gooit revit in mijn&nbsp;project de tag gelijk weg als ik deze wil copieeren. Het&nbsp;project is gedeeld. Heeft iemand een oplossing?</p>\r\n<p>alvast bedankt, &nbsp;Eelco</p>\r\n', '', '1359626341', '2');
INSERT INTO `node_revisions` VALUES ('3416', '3418', '178', 'teksten schuin uitlijnen', '<p>Een tip om&nbsp;(veel de zelfde) teksten tegen een schuinen lijn uit te lijnen. Het alternatief&nbsp;is dat je elke tekst&nbsp;los moet roteren.</p>\r\n<p>1.Zet de tekst&nbsp;gewoon&nbsp;neer.<br />\r\n	2.Teken er een&nbsp;horizontaal stukje lijn onder of boven<br />\r\n	3.Maak van de tekst en de lijn een groepje<br />\r\n	4. allign&nbsp;het groepje, door het stukje&nbsp;lijn&nbsp;te&nbsp;gebruiken<br />\r\n	(5. evt je groepjes ungroupen)</p>\r\n<p>Eelco</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/gebruiker/178/allign%20tekst.jpg\" style=\"width: 640px; height: 333px;\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n', '<p>Een tip om&nbsp;(veel de zelfde) teksten tegen een schuinen lijn uit te lijnen. Het alternatief&nbsp;is dat je elke tekst&nbsp;los moet roteren.</p>', '', '1359641341', '2');
