INSERT INTO `node_revisions` VALUES ('3935', '3937', '505', 'Bartels Algemeen stempel V2.0 (dynamische stempel)', '<p>Beste Collega&#39;s,</p>\r\n<p>De algemene stempels in het stempelpallet zullen vanaf morgen veranderd zijn in dynamische stempels.</p>\r\n<p>Op het verzoek van collega&#39;s is de stempel aangepast naar een invulformulier met vervolgens een automatisch opgemaakte&nbsp;stempel.</p>\r\n<p>&nbsp;</p>\r\n<p>Er zullen 3 invulvelden zichtbaar zijn:</p>\r\n<p>(zie onderstaand)</p>\r\n<p>- Projectnummer</p>\r\n<p>- Gecontroleerd door [Initialten, volledige naam alles mag/kan]</p>\r\n<p>- Datum [Standaard op de dag van vandaag, anders invullen ook mogelijk]</p>\r\n<p>&nbsp;</p>\r\n<p>Hopelijk voldoe ik hiermee niet alleen aan de wens van velen hier in utrecht maar ook aan de wensen van heel Bartels.</p>\r\n<p>&nbsp;</p>\r\n<p>Groeten,</p>\r\n<p>Tom</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;&nbsp;<img alt=\"\" src=\"/sites/default/files/gebruiker/505/1e_0.PNG\" style=\"width: 503px; height: 185px;\" /></p>\r\n<p><img alt=\"\" src=\"/sites/default/files/gebruiker/505/2e.PNG\" style=\"width: 504px; height: 185px;\" /></p>\r\n<p><img alt=\"\" src=\"/sites/default/files/gebruiker/505/3e.PNG\" style=\"width: 509px; height: 188px;\" /></p>\r\n<p><img alt=\"\" src=\"/sites/default/files/gebruiker/505/stempel.PNG\" style=\"width: 640px; height: 447px;\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n', '<p>Beste Collega&#39;s,</p>\r\n<p>De algemene stempels in het stempelpallet zullen vanaf morgen veranderd zijn in dynamische stempels.</p>\r\n<p>Op het verzoek van collega&#39;s is de stempel aangepast naar een invulformulier met vervolgens een automatisch opgemaakte&nbsp;stempel.</p>\r\n<p>&nbsp;</p>\r\n<p>Er zullen 3 invulvelden zichtbaar zijn:</p>\r\n<p>(zie onderstaand)</p>\r\n<p>- Projectnummer</p>', '', '1429796738', '2');
