INSERT INTO `node_revisions` VALUES ('3834', '3836', '32', 'Doe mee met het Grote BinnenBartels Tourspel!', '<p>Beste collegae,&nbsp;Alle Tourploegenhebben hun startlijst vrijgegeven, dus het puzzelen kan&nbsp;beginnen!&nbsp;Dit jaar staat&nbsp;er een jubileumeditie op het programma, het Tourspel wordt al voor&nbsp;de 10e keer gespeeld!</p>\r\n<p>Hieronder kun je&nbsp;het invulformulier voor de Jubileumeditie van het Groot&nbsp;BinnenBartels Tourspel.&nbsp;Ook de spelregels zijn&nbsp;hier te vinden.&nbsp;Graag het &nbsp;Excel-bestand gebruiken om in te vullen, en dan het bestand&nbsp;<strong>VOOR&nbsp;vrijdag 13.00uur</strong> mailen&nbsp;naar <a href=\"mailto:smulder@bartels.nl\">Sven Mulder</a>.&nbsp;</p>\r\n<p>Middels uitklapmenu&#39;s kunnen in de gele cellen de namen van de renners&nbsp;worden ingevuld.</p>\r\n<p>Voor deze&nbsp;jubileumeditie hopen we dan&nbsp;ook een&nbsp;record aantal deelnemers. Deelname is geheel gratis dus doe nu mee en verdien eeuwige roem!</p>\r\n<p>Sven Mulder (Bartels Eindhoven)</p>\r\n', '<p>Beste collegae,&nbsp;Alle Tourploegenhebben hun startlijst vrijgegeven, dus het puzzelen kan&nbsp;beginnen!&nbsp;Dit jaar staat&nbsp;er een jubileumeditie op het programma, het Tourspel wordt al voor&nbsp;de 10e keer gespeeld!</p>', '', '1404293296', '2');
