INSERT INTO `node_revisions` VALUES ('3845', '3847', '32', 'Winnaar Tourspel uit Eindhoven', '<p>Beste Tourvolgers,</p>\r\n<p><span style=\"font-size: 12px;\">Hierbij </span>alweer<span style=\"font-size: 12px;\"> </span>het<span style=\"font-size: 12px;\"> </span>laatste<span style=\"font-size: 12px;\"> bulletin van </span>ons<span style=\"font-size: 12px;\"> </span>Tourspel<span style=\"font-size: 12px;\">.</span></p>\r\n<p><span style=\"font-size: 12px;\">Eerst de </span>etappe<span style=\"font-size: 12px;\"> van </span>vrijdag<span style=\"font-size: 12px;\">: </span>een<span style=\"font-size: 12px;\"> </span>eenzame<span style=\"font-size: 12px;\"> </span>vluchter<span style=\"font-size: 12px;\"> </span>klopt<span style=\"font-size: 12px;\"> </span>het<span style=\"font-size: 12px;\"> </span>jagende&nbsp;<span style=\"font-size: 12px;\">peloton: </span>Navardauskas<span style=\"font-size: 12px;\"> </span>wint<span style=\"font-size: 12px;\"> de </span>rit<span style=\"font-size: 12px;\">, </span>maar<span style=\"font-size: 12px;\"> is </span>niet<span style=\"font-size: 12px;\"> </span>gekozen<span style=\"font-size: 12px;\"> in </span>het<span style=\"font-size: 12px;\"> </span>Tourspel<span style=\"font-size: 12px;\">.&nbsp;</span><span style=\"font-size: 12px;\">Jan </span>Bloten<span style=\"font-size: 12px;\"> (</span>Enschede<span style=\"font-size: 12px;\">) </span>wint<span style=\"font-size: 12px;\"> de </span>etappe<span style=\"font-size: 12px;\">! Henri van </span>Nistelrooy<span style=\"font-size: 12px;\"> en Edwin </span>Kroes&nbsp;<span style=\"font-size: 12px;\">lopen door nr. 2 John </span>Degenkolb<span style=\"font-size: 12px;\"> (</span>fors<span style=\"font-size: 12px;\">) </span>uit<span style=\"font-size: 12px;\"> op Jan </span>Willems<span style=\"font-size: 12px;\">.</span></p>\r\n<p><span style=\"font-size: 12px;\">Op </span>zaterdag<span style=\"font-size: 12px;\"> de </span>enige<span style=\"font-size: 12px;\"> </span>tijdrit<span style=\"font-size: 12px;\"> in </span>deze<span style=\"font-size: 12px;\"> Tour. </span>Wereldkampioen<span style=\"font-size: 12px;\"> Tony Martin </span>wint<span style=\"font-size: 12px;\">,&nbsp;</span><span style=\"font-size: 12px;\">voor </span>onze<span style=\"font-size: 12px;\"> </span>eigen<span style=\"font-size: 12px;\"> trots Tom </span>Dumoulin<span style=\"font-size: 12px;\">, Jan </span>Barta<span style=\"font-size: 12px;\"> en </span>Vincenzo<span style=\"font-size: 12px;\"> </span>Nibali<span style=\"font-size: 12px;\">.&nbsp;</span><span style=\"font-size: 12px;\">Bart van de </span>Wouw<span style=\"font-size: 12px;\"> </span>wint<span style=\"font-size: 12px;\"> de </span>tijdrit<span style=\"font-size: 12px;\">, door de nu,,</span>er<span style=\"font-size: 12px;\"> 1, 2 en 4 in de </span>ploeg<span style=\"font-size: 12px;\"> </span>te&nbsp;<span style=\"font-size: 12px;\">hebben. </span>Klassementsleider<span style=\"font-size: 12px;\"> Henri van </span>Nistelrooy<span style=\"font-size: 12px;\"> </span>kan<span style=\"font-size: 12px;\"> de </span>eindoverwinning<span style=\"font-size: 12px;\"> </span>bijna&nbsp;<span style=\"font-size: 12px;\">niet </span>meer<span style=\"font-size: 12px;\"> </span>ontgaan<span style=\"font-size: 12px;\">, </span>doordat<span style=\"font-size: 12px;\"> </span>hij<span style=\"font-size: 12px;\"> met </span>zowel<span style=\"font-size: 12px;\"> Tony Martin </span>als<span style=\"font-size: 12px;\"> met </span>Tejay<span style=\"font-size: 12px;\"> van&nbsp;</span><span style=\"font-size: 12px;\">Garderen </span>uitloopt<span style=\"font-size: 12px;\"> op </span>alle<span style=\"font-size: 12px;\"> </span>directe<span style=\"font-size: 12px;\"> </span>concurrentie<span style=\"font-size: 12px;\">.</span></p>\r\n<p><span style=\"font-size: 12px;\">De </span>showsprint<span style=\"font-size: 12px;\"> op </span>zondag<span style=\"font-size: 12px;\"> </span>eindigt<span style=\"font-size: 12px;\"> in de </span>vierde<span style=\"font-size: 12px;\"> </span>etappezege<span style=\"font-size: 12px;\"> </span>voor<span style=\"font-size: 12px;\"> Marcel </span>Kittel<span style=\"font-size: 12px;\">.&nbsp;</span><span style=\"font-size: 12px;\">De </span>nummers<span style=\"font-size: 12px;\"> </span>twee<span style=\"font-size: 12px;\"> en </span>drie<span style=\"font-size: 12px;\"> </span>uit<span style=\"font-size: 12px;\"> </span>het<span style=\"font-size: 12px;\"> </span>eindklassement<span style=\"font-size: 12px;\"> (Jan </span>Willems<span style=\"font-size: 12px;\"> en Edwin </span>Kroes<span style=\"font-size: 12px;\">)&nbsp;</span><span style=\"font-size: 12px;\">delen de </span>etappezege<span style=\"font-size: 12px;\">, </span>maar<span style=\"font-size: 12px;\"> </span>kunnen<span style=\"font-size: 12px;\"> Henri van </span>Nistelrooy<span style=\"font-size: 12px;\"> </span>niet<span style=\"font-size: 12px;\"> </span>meer<span style=\"font-size: 12px;\"> van de&nbsp;</span><span style=\"font-size: 12px;\">eindoverwinning </span>afhouden<span style=\"font-size: 12px;\">.</span></p>\r\n<p>Gefeliciteerd Henri!&nbsp;<span style=\"font-size: 12px;\">De </span>prijs<span style=\"font-size: 12px;\"> </span>blijft<span style=\"font-size: 12px;\"> </span>nog<span style=\"font-size: 12px;\"> </span>een<span style=\"font-size: 12px;\"> </span>jaar<span style=\"font-size: 12px;\"> in </span>Eindhoven<span style=\"font-size: 12px;\">.</span></p>\r\n<p><span style=\"font-size: 12px;\">De </span>poedelprijs<span style=\"font-size: 12px;\"> </span>gaat<span style=\"font-size: 12px;\">, </span>dankzij<span style=\"font-size: 12px;\"> </span>toch<span style=\"font-size: 12px;\"> erg </span>veel<span style=\"font-size: 12px;\"> </span>pech<span style=\"font-size: 12px;\">, </span>naar<span style=\"font-size: 12px;\"> </span>trouwe<span style=\"font-size: 12px;\"> </span>deelnemer&nbsp;<span style=\"font-size: 12px;\">Carla </span>Winkel<span style=\"font-size: 12px;\">.</span></p>\r\n<p><span style=\"font-size: 12px;\">Henri van </span>Nistelrooy<span style=\"font-size: 12px;\"> en Leno Tromp </span>halen<span style=\"font-size: 12px;\"> de finish met de </span>meeste<span style=\"font-size: 12px;\"> </span>renners<span style=\"font-size: 12px;\">.&nbsp;</span><span style=\"font-size: 12px;\">Zij </span>hebben<span style=\"font-size: 12px;\"> </span>slechts<span style=\"font-size: 12px;\"> </span>drie<span style=\"font-size: 12px;\"> </span>uitvallers<span style=\"font-size: 12px;\">.&nbsp;</span><span style=\"font-size: 12px;\">Metske </span>Kooistra<span style=\"font-size: 12px;\">, Tom van </span>der<span style=\"font-size: 12px;\"> </span>Pouw<span style=\"font-size: 12px;\">, Mark </span>ter<span style=\"font-size: 12px;\"> </span>Steege<span style=\"font-size: 12px;\">, Gerard </span>Steenbeek<span style=\"font-size: 12px;\"> en&nbsp;</span><span style=\"font-size: 12px;\">Erik </span>Seegers<span style=\"font-size: 12px;\"> </span>hebben<span style=\"font-size: 12px;\"> </span>het<span style=\"font-size: 12px;\"> </span>meest<span style=\"font-size: 12px;\"> </span>pech<span style=\"font-size: 12px;\">: met </span>zeven<span style=\"font-size: 12px;\"> </span>uitvallers<span style=\"font-size: 12px;\"> </span>halen<span style=\"font-size: 12px;\"> </span>maar<span style=\"font-size: 12px;\"> </span>acht&nbsp;<span style=\"font-size: 12px;\">renners </span>uit<span style=\"font-size: 12px;\"> </span>hun<span style=\"font-size: 12px;\"> </span>ploeg<span style=\"font-size: 12px;\"> de finish in </span>Parijs<span style=\"font-size: 12px;\">.</span></p>\r\n<p><span style=\"font-size: 12px;\">Onderstaand </span>ingevoegd<span style=\"font-size: 12px;\"> de </span>samenstelling<span style=\"font-size: 12px;\"> van </span>het<span style=\"font-size: 12px;\"> </span>ideale<span style=\"font-size: 12px;\"> team (doe </span>er<span style=\"font-size: 12px;\"> je&nbsp;</span><span style=\"font-size: 12px;\">voordeel </span>mee<span style=\"font-size: 12px;\">!) en de </span>winnende<span style=\"font-size: 12px;\"> </span>lijst<span style=\"font-size: 12px;\"> van Henri:</span></p>\r\n<p><span style=\"font-size: 12px;\"><img alt=\"\" src=\"/sites/default/files/file/nieuws/pic25541.gif\" style=\"width: 301px; height: 281px;\" /></span></p>\r\n', '<p>Beste Tourvolgers,</p>\r\n<p><span style=\"font-size: 12px;\">Hierbij </span>alweer<span style=\"font-size: 12px;\"> </span>het<span style=\"font-size: 12px;\"> </span>laatste<span style=\"font-size: 12px;\"> bulletin van </span>ons<span style=\"font-size: 12px;\"> </span>Tourspel<span style=\"font-size: 12px;\">.</span></p>', '', '1406708175', '2');
