INSERT INTO `node_revisions` VALUES ('3849', '3851', '32', 'Rouwkaart vrouw Foppe Haasnoot', '<p>Beste collega&#39;s,</p>\r\n<p>Afgelopen donderdag is de vrouw van Foppe Haasnoot, Astrid, vredig ingeslapen&nbsp;na een&nbsp;heftige&nbsp;periode van ziekte.&nbsp;</p>\r\n<p>Foppe heeft aangegeven het zeer op prijs te stellen wanneer collega&#39;s aanwezig zijn op de afscheidsbijeenkomst, a.s. donderdagmiddag in Lemelerveld.&nbsp;</p>\r\n<p>In de bijlage vind je de rouwkaart met alle informatie en adresgegevens.&nbsp;</p>\r\n<p>Bij vragen kun je contact opnemen met Moniek Vrielink of Pieter van Boom.&nbsp;</p>\r\n', '<p>Beste collega&#39;s,</p>\r\n<p>Afgelopen donderdag is de vrouw van Foppe Haasnoot, Astrid, vredig ingeslapen&nbsp;na een&nbsp;heftige&nbsp;periode van ziekte.&nbsp;</p>\r\n<p>Foppe heeft aangegeven het zeer op prijs te stellen wanneer collega&#39;s aanwezig zijn op de afscheidsbijeenkomst, a.s. donderdagmiddag in Lemelerveld.&nbsp;</p>', '', '1410947258', '2');
INSERT INTO `node_revisions` VALUES ('3859', '3861', '32', 'Wijzigingen Elst en Enschede', '<p>In zowel Elst als&nbsp;Enschede hebben afgelopen periode&nbsp;wijzigingen plaatsgevonden op het gebied van de vestigingsdirectie.</p>\r\n<p><strong>Bartels Elst</strong><br />\r\n	<span style=\"font-size: 12px;\">Het </span>afgelopen<span style=\"font-size: 12px;\"> </span>jaar<span style=\"font-size: 12px;\"> </span>werd<span style=\"font-size: 12px;\"> </span>Bartels<span style=\"font-size: 12px;\"> </span>Elst<span style=\"font-size: 12px;\"> </span>aangestuurd<span style=\"font-size: 12px;\"> door </span>Hennie<span style=\"font-size: 12px;\"> </span>Weener<span style=\"font-size: 12px;\">&nbsp;(</span>vestigingsdirecteur<span style=\"font-size: 12px;\"> </span>Apeldoorn<span style=\"font-size: 12px;\"> en </span>Elst)<span style=\"font-size: 12px;\">&nbsp;in </span>samenwerking<span style=\"font-size: 12px;\"> met Rene van Brandenburg. </span>Na<span style=\"font-size: 12px;\"> </span>het<span style=\"font-size: 12px;\"> </span>vertrek<span style=\"font-size: 12px;\"> van Rene van Brandenburg </span>deze<span style=\"font-size: 12px;\"> </span>zomer<span style=\"font-size: 12px;\"> is </span>besloten<span style=\"font-size: 12px;\"> </span>om<span style=\"font-size: 12px;\"> de </span>aansturing<span style=\"font-size: 12px;\"> </span>weer<span style=\"font-size: 12px;\"> </span>vanuit<span style=\"font-size: 12px;\"> </span>Elst<span style=\"font-size: 12px;\"> </span>zelf<span style=\"font-size: 12px;\"> </span>te<span style=\"font-size: 12px;\"> </span>laten<span style=\"font-size: 12px;\"> </span>verlopen<span style=\"font-size: 12px;\">. Anton de Boer neemt de functie van vestigingsdirecteur tijdelijk waar,&nbsp;</span>om<span style=\"font-size: 12px;\"> de </span>dagelijkse<span style=\"font-size: 12px;\"> </span>leiding<span style=\"font-size: 12px;\"> </span>te<span style=\"font-size: 12px;\"> </span>verzorgen<span style=\"font-size: 12px;\">. </span>Hij<span style=\"font-size: 12px;\"> is </span>dan<span style=\"font-size: 12px;\"> </span>ook<span style=\"font-size: 12px;\"> </span>het<span style=\"font-size: 12px;\"> </span>aanspreekpunt<span style=\"font-size: 12px;\"> </span>voor<span style=\"font-size: 12px;\"> </span>opdrachtgevers<span style=\"font-size: 12px;\"> en </span>voor<span style=\"font-size: 12px;\"> de </span>andere<span style=\"font-size: 12px;\"> </span>vestigingen<span style=\"font-size: 12px;\">.&nbsp;</span><span style=\"font-size: 12px;\">Ondertussen&nbsp;</span>wordt<span style=\"font-size: 12px;\"> </span>er<span style=\"font-size: 12px;\">&nbsp;</span>gezocht<span style=\"font-size: 12px;\"> </span>naar<span style=\"font-size: 12px;\"> </span>een<span style=\"font-size: 12px;\"> </span>structurele<span style=\"font-size: 12px;\"> </span>oplossing<span style=\"font-size: 12px;\">.&nbsp;</span></p>\r\n<p><strong>Bartels Enschede</strong><br />\r\n	In Enschede is Stefan Droste, de huidige vestigingsdirecteur, al enige tijd&nbsp;afwezig door ziekte. Zijn taken worden tot nader order waargenomen door Taco Klevering, die een aantal dagen per week ook in Enschede op de vestiging aanwezig is. Taco is &nbsp;het aanspreekpunt voor de overige vestigingen en de medewerkers van Enschede.</p>\r\n<p>Voor vragen over bovenstaande mededelingen&nbsp;kun je terecht bij Pieter van Boom.&nbsp;</p>\r\n', '<p>In zowel Elst als&nbsp;Enschede hebben afgelopen periode&nbsp;wijzigingen plaatsgevonden op het gebied van de vestigingsdirectie.</p>', '', '1410959208', '2');
INSERT INTO `node_revisions` VALUES ('3852', '3854', '184', 'schoorpaal met aan te passen schoorsymbool?', '<p>Is er ergens een schoorpaal waarvan de de richting van het schoren makkelijk aan&nbsp;kan geven? Nu moet ik de hele paal draaien en kloppen de symbolen niet als&nbsp;ik een Q1 of een Q13 heb want die draaien nu mee.</p>\r\n<p>Ik hoor het&nbsp;graag, Carla</p>\r\n', '<p>Is er ergens een schoorpaal waarvan de de richting van het schoren makkelijk aan&nbsp;kan geven? Nu moet ik de hele paal draaien en kloppen de symbolen niet als&nbsp;ik een Q1 of een Q13 heb want die draaien nu mee.</p>\r\n<p>Ik hoor het&nbsp;graag, Carla</p>\r\n', '', '1410339177', '2');
