INSERT INTO `node_revisions` VALUES ('3929', '3931', '1', 'Eindhoven tijdelijk telefonisch slecht bereikbaar', '<p>Wegens een telefoonstoring is onze vestiging in Eindhoven momenteel niet bereikbaar via het vaste nummer.</p>\r\n<p>Direct contact met onze vestiging kan via een van de projectleiders:</p>\r\n<ul>\r\n	<li>\r\n		Robin van Gerwen 06-12549482</li>\r\n	<li>\r\n		Roel Strijbos 06-23652035</li>\r\n	<li>\r\n		Henri van Nistelrooy 06-12323106</li>\r\n</ul>\r\n<p>Er wordt hard gewerkt om de problemen op te lossen. Excuses voor het ongemak.</p>\r\n', '<p>Wegens een telefoonstoring is onze vestiging in Eindhoven momenteel niet bereikbaar via het vaste nummer.</p>\r\n<p>Direct contact met onze vestiging kan via een van de projectleiders:</p>', '', '1427276166', '2');
