INSERT INTO `node_revisions` VALUES ('4037', '4039', '32', 'Symposium Gezonde Leefomgeving - LievenseCSO Milieu', '<div>\r\n	<img alt=\"\" src=\"/sites/default/files/file/nieuws/Uitnodiging%20Seminar%20Gezonde%20leefomgeving%202%20DEF_0.jpg\" style=\"width: 600px; height: 433px;\" /></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Op 24 maart organiseert LievenseCSO Milieu een symposium over een Gezonde Leefomgeving. Diverse klanten worden uitgenodigd, maar we willen ook graag potenti&euml;le klanten een kans geven om te komen. Daarom promoten we het evenement ook via onze social media kanalen en website (van zowel Bartels als LievenseCSO).&nbsp;</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Bij deze ook een verzoek aan jullie om het in je netwerk te delen! Je kunt het bericht delen via <a href=\"https://twitter.com/bartelsglobal\" target=\"_blank\">twitter</a>&nbsp;en <a href=\"https://www.linkedin.com/company/bartels\" target=\"_blank\">LinkedIn</a>. Alvast bedankt!&nbsp;</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<a href=\"http://www.lievensecso.com/nl/Over-ons/Aanmelden-symposium-Gezonde-Leefomgeving\" target=\"_blank\">Lees meer op de website van LievenseCSO over het symposium!</a></div>\r\n', '<div>\r\n	<img alt=\"\" src=\"/sites/default/files/file/nieuws/Uitnodiging%20Seminar%20Gezonde%20leefomgeving%202%20DEF_0.jpg\" style=\"width: 600px; height: 433px;\" /></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r', '', '1454408213', '2');
INSERT INTO `node_revisions` VALUES ('4038', '4040', '184', 'linked file in model naar ifc', '<p>Ik heb een model met daarin een paar linked models (vakwerken die veel&nbsp;voorkomen heb ik dus in een apart model gezet). Nu moet dit ook als&nbsp;ifc naar de klant toe, hoe kan ik dit het beste&nbsp;doen? &nbsp;Ik zag bij de options van ifc export een vinkje voor Export linked files as seperate IFC&#39;s. Die heb&nbsp;ik nu gebruikt en dan krijg ik ipv 1x een vakwerk, 10x een&nbsp;ifc bestand van het vakwerk aangezien deze 10x voorkomt in het hoofdmodel.&nbsp;</p>\r\n<p>Andere optie is van de losse modellen zelf een&nbsp;ifc maken en ook meesturen.</p>\r\n<p>Het blijven&nbsp;dus&nbsp;aparte bestanden, kan dit ook handiger?&nbsp;</p>\r\n', '<p>Ik heb een model met daarin een paar linked models (vakwerken die veel&nbsp;voorkomen heb ik dus in een apart model gezet). Nu moet dit ook als&nbsp;ifc naar de klant toe, hoe kan ik dit het beste&nbsp;doen? &nbsp;Ik zag bij de options van ifc export een vinkje voor Export linked files as seperate IFC&#39;s.', '', '1454485097', '2');
