INSERT INTO `node_revisions` VALUES ('4040', '4042', '1', 'Nieuw OR reglement', '<p>Er zijn sinds het oude OR reglement&nbsp;uit 2002 de nodige wijzigingen en veranderingen geweest. Daarom heeft de OR een nieuw reglement&nbsp;opgesteld, dat vind je op de pagina <a href=\"/afdelingen/or/reglement\">Afdelingen &gt; OR &gt; Reglement</a>.</p>\r\n<p>Voor vragen hierover kun je terecht bij het OR lid op je vestiging.&nbsp;</p>\r\n', '<p>Er zijn sinds het oude OR reglement&nbsp;uit 2002 de nodige wijzigingen en veranderingen geweest. Daarom heeft de OR een nieuw reglement&nbsp;opgesteld, dat vind je op de pagina <a href=\"/afdelingen/or/reglement\">Afdelingen &gt; OR &gt; Reglement</a>.</p>\r\n<p>Voor vragen hierover kun je terecht bij het OR lid op je vestiging.&nbsp;</p>\r\n', '', '1454504889', '2');
INSERT INTO `node_revisions` VALUES ('4041', '4043', '1', 'Inspectieplicht galerijflats', '<p>Sinds 1 januari hebben gebouweigenaren van galerijflats met galerijen en/of uitkragende&nbsp;balkons, gebouwd&nbsp;in de jaren 50 t/m 70, een inspectieplicht voor hun&nbsp;uitkragende vloeren.</p>\r\n<p>Om die reden hebben we vanuit de afdeling Bouwadvies zojuist een mailing verstuurd naar klanten wie dit mogelijk aan gaat.</p>\r\n<p><a href=\"http://us4.campaign-archive2.com/?u=a13d542c78e28e50d01860f3e&amp;id=e3995e9750\" target=\"_blank\">Je kunt de nieuwsbrief hier lezen!</a></p>\r\n<p>Mocht je nog klanten hebben&nbsp;voor wie dit interessant kan zijn, of vragen krijgen, verwijs ze dan door naar deze&nbsp;nieuwsbrief en de contactpersonen die hierin staan.</p>\r\n', '<p>Sinds 1 januari hebben gebouweigenaren van galerijflats met galerijen en/of uitkragende&nbsp;balkons, gebouwd&nbsp;in de jaren 50 t/m 70, een inspectieplicht voor hun&nbsp;uitkragende vloeren.</p>\r\n<p>Om die reden hebben we vanuit de afdeling Bouwadvies zojuist een mailing verstuurd naar klanten wie dit mogelijk aan gaat.</p>', '', '1454582665', '2');
