INSERT INTO `node_revisions` VALUES ('4123', '4125', '32', 'Gebruik nieuw intranet', '<p>Afgelopen week is het nieuwe&nbsp;intranet gelanceerd voor de complete LievenseCSO Groep, dus ook voor alle Bartels&nbsp;medewerkers.</p>\r\n<p>Dat betekent dat het oude intranet van Bartels, dit infonet, zal gaan verdwijnen. De content op het infonet gaan we grotendeels overzetten&nbsp;naar het nieuwe&nbsp;intranet, wat betekent dat binnen ongeveer 2 a 3 weken het infonet&nbsp;uit de lucht zal gaan.</p>\r\n<p>Heb je nog vragen, opmerkingen of verbeterpunten? <a href=\"mailto:mvrielink@bartels.nl\">Meld dit dan bij Moniek Vrielink</a>.</p>\r\n<p><a href=\"/sites/default/files/file/nieuws/2016-09%20Handleiding%20Intranet%20LievenseCSO%20Groep.pdf\">De handleiding voor het nieuwe&nbsp;intranet kun je hier downloaden.</a></p>\r\n<p><a href=\"http://www.lcb-intranet.nl\">Klik hier om&nbsp;naar het nieuwe intranet te gaan!</a></p>\r\n', '<p>Afgelopen week is het nieuwe&nbsp;intranet gelanceerd voor de complete LievenseCSO Groep, dus ook voor alle Bartels&nbsp;medewerkers.</p>', '', '1475675112', '2');
